```typescript // Template metadata structure interface Template { id: string; name: string; description: string; category: 'development' | 'deployment' | 'testing' | 'documentation' | 'custom'; icon: string; // emoji or unicode character author: { name: string; avatar?: string; verified: boolean; }; stats: { rating: number; // 0-5 downloads: number; reviews: number; lastUpdated: Date; }; requirements: { tools?: string[]; // Required CLI tools runtime?: string; // Node version, Python version, etc. platform?: string[]; // OS compatibility }; sections: Array<{ name: string; stepCount: number; estimated_time: string; }>; variables: Array<{ name: string; type: string; required: boolean; default?: any; description: string; validation?: string; // Regex or validation rule }>; tags: string[]; version: string; source: 'official' | 'community' | 'private' | 'local'; }
// Search and filter system interface TemplateFilter { search?: string; category?: string; difficulty?: 'beginner' | 'intermediate' | 'advanced'; duration?: '<30' | '30-60' | '>60'; // minutes source?: string[]; hasRating?: number; // minimum rating sortBy?: 'popular' | 'recent' | 'rating' | 'alphabetical'; }
// Template gallery renderer class TemplateGallery { renderCard(template: Template, selected: boolean): string { const rating = '‚≠ê'.repeat(Math.floor(template.stats.rating)); const highlight = selected ? ansi.inverse : '';
